		'============================================================================*/',
		'#if (FXAA_PC == 1)',
		'/*--------------------------------------------------------------------------*/',
		'FxaaFloat4 FxaaPixelShader(',
		'    //',
		'    // Use noperspective interpolation here (turn off perspective interpolation).',
		'    // {xy} = center of pixel',
		'    FxaaFloat2 pos,',
		'    //',
		'    // Used only for FXAA Console, and not used on the 360 version.',
		'    // Use noperspective interpolation here (turn off perspective interpolation).',
		'    // {xy_} = upper left of pixel',
