		'    // {_y_} = -N/screenHeightInPixels',
		'    // {_z_} =  N/screenWidthInPixels',
		'    // {__w} =  N/screenHeightInPixels',
		'    FxaaFloat4 fxaaConsoleRcpFrameOpt,',
		'    //',
		'    // Only used on FXAA Console.',
